// Describe this function...
function DATA_ME() {
  comic_data = {};
  dictSet(comic_data, 'pages', typeof guide_page_guide == 'object' ? Object.keys(guide_page_guide) : []);
  dictSet(comic_data, 'final_guide_frame', 1560);
  dictSet(comic_data, 'guide_keyframe_rate', guide_rate);
  guide_pages = [];
  var j_list = dictGet(comic_data, 'pages');
  for (var j_index in j_list) {
    j = j_list[j_index];
    guide_pages.push(dictGet(guide_page_guide, j));
  }
  dictSet(comic_data, 'guide_pages', guide_pages);
  guide_frame_keyframes = [];
  for (m = 0; m <= 1560; m += 60) {
    guide_frame_keyframes.push(m);
  }
  dictSet(comic_data, 'guide_frame_keyframes', guide_frame_keyframes);
  return comic_data;
}


